@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));
  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));
  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));
  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));
  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));
  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));
  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));
  --color-destructive: hsl(var(--destructive));
  --color-destructive-foreground: hsl(var(--destructive-foreground));
  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));

  --color-gold: hsl(var(--gold));
  --color-gold-foreground: hsl(var(--gold-foreground));
  --color-gold-light: hsl(var(--gold-light));
  --color-silver: hsl(var(--silver));

  --font-sans: 'Inter', 'Poppins', sans-serif;
  --font-serif: 'Playfair Display', 'Cormorant Garamond', serif;
  --font-display: 'Cinzel', serif;

  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
}

:root {
  /* Dark Luxury Theme Base */
  --background: 0 0% 5%; /* #0C0C0C */
  --foreground: 46 30% 95%;

  --card: 0 0% 8%;
  --card-foreground: 46 30% 95%;

  --popover: 0 0% 8%;
  --popover-foreground: 46 30% 95%;

  --primary: 46 65% 52%; /* #D4AF37 Gold */
  --primary-foreground: 0 0% 10%;

  --secondary: 0 0% 15%;
  --secondary-foreground: 0 0% 90%;

  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 60%;

  --accent: 46 65% 52%;
  --accent-foreground: 0 0% 10%;

  --destructive: 0 62% 30%;
  --destructive-foreground: 0 0% 98%;

  --border: 46 30% 20%;
  --input: 46 30% 20%;
  --ring: 46 65% 52%;

  --gold: 46 65% 52%;
  --gold-foreground: 0 0% 5%;
  --gold-light: 46 70% 65%;
  --silver: 0 0% 75%;

  --radius: 0.25rem;
}

@layer base {
  * {
    @apply border-border;
  }
  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
  h1, h2, h3, h4, h5, h6 {
    @apply font-serif tracking-tight;
  }
}

@layer utilities {
  .text-gold {
    color: hsl(var(--gold));
  }
  .bg-gold {
    background-color: hsl(var(--gold));
  }
  .border-gold {
    border-color: hsl(var(--gold));
  }

  .text-gradient-gold {
    background: linear-gradient(to right, #BF953F, #FCF6BA, #B38728, #FBF5B7, #AA771C);
    -webkit-background-clip: text;
    background-clip: text;
    color: transparent;
  }

  .bg-gradient-gold {
    background: linear-gradient(135deg, #BF953F, #FCF6BA, #B38728, #FBF5B7, #AA771C);
  }

  .shine-effect {
    position: relative;
    overflow: hidden;
  }

  .shine-effect::after {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 50%;
    height: 100%;
    background: linear-gradient(to right, rgba(255,255,255,0) 0%, rgba(255,255,255,0.2) 50%, rgba(255,255,255,0) 100%);
    transform: skewX(-25deg);
    transition: 0.5s;
  }

  .shine-effect:hover::after {
    left: 150%;
    transition: 0.7s ease-in-out;
  }
}

